<?php

namespace SuperbAddons\Gutenberg\Controllers;

defined('ABSPATH') || exit();

/**
 * Icons in our list are only added if they don't already exist in WordPress core,
 * ensuring forward compatibility when WordPress adds these icons natively.
 */
class GutenbergSocialIconsController
{
    /**
     * Minimum WordPress version required for this feature.
     * The filter 'block_core_social_link_get_services' was added in WP 6.9.
     */
    const MINIMUM_WP_VERSION = '6.9';

    const SOCIAL_LINKS_STYLE_HANDLE = 'superb-addons-social-links';

    private static $addons_social_links;

    public static function Initialize()
    {
        if (!self::IsWPVersionCompatible()) {
            return;
        }

        add_action('init', [__CLASS__, 'RegisterStyles']);

        // Server-side filter to add custom icons for frontend rendering
        add_filter('block_core_social_link_get_services', [__CLASS__, 'AddCustomSocialServices'], 10, 1);

        add_action('wp_enqueue_scripts', [__CLASS__, 'EnqueueFrontendStyles'], PHP_INT_MAX);

        add_action('enqueue_block_editor_assets', [__CLASS__, 'EnqueueEditorAssets']);
    }

    public static function RegisterStyles()
    {
        wp_register_style(self::SOCIAL_LINKS_STYLE_HANDLE, false, array(), SUPERBADDONS_VERSION);

