    private static function IsWPVersionCompatible()
    {
        global $wp_version;
        return version_compare($wp_version, self::MINIMUM_WP_VERSION, '>=');
    }

    public static function AddCustomSocialServices($services_data)
    {
        if (!self::$addons_social_links) {
            return $services_data;
        }

        foreach (self::$addons_social_links as $service_key => $service_data) {
            // Only add if the service doesn't already exist in WordPress
            // This ensures our plugin doesn't interfere when WP adds these icons later
            if (!isset($services_data[$service_key])) {
                $services_data[$service_key] = $service_data;
            }
        }

        return $services_data;
    }


    public static function EnqueueEditorAssets()
    {
        wp_enqueue_script(
            'superb-addons-social-icons',
            SUPERBADDONS_ASSETS_PATH . '/js/gutenberg/social-icons.js',
            ['wp-blocks', 'wp-element', 'wp-primitives', 'wp-i18n', 'wp-dom-ready'],
            SUPERBADDONS_VERSION,
            true
        );

        self::EnqueueSocialIconStyles();
    }


    private static function EnqueueSocialIconStyles()
    {
        // Ensure the function exists (WordPress 6.9+)
        if (!function_exists('block_core_social_link_services')) {
            return;
        }

        // Get existing services from WordPress core to filter out already-supported icons
        $existing_services = block_core_social_link_services();
        $css = '';
        foreach ($existing_services as $service_key => $service_data) {
            // Only add CSS for icons not already in WordPress core
            if (!isset($service_data['superb-addons-icon-color'])) {
                continue;
            }

            $safe_key = sanitize_key($service_key);
            $safe_color = sanitize_hex_color($service_data['superb-addons-icon-color']);

            // Skip if sanitization fails
            if (empty($safe_color) || empty($safe_key)) {
                continue;
            }

            // Default style (with background color)
            $css .= ":where(.wp-block-social-links:not(.is-style-logos-only)) .wp-social-link-{$safe_key} {";
            $css .= "background-color: {$safe_color};";
            $css .= "color: #fff;";
            $css .= "}";

            // Logos-only style
            $css .= ":where(.wp-block-social-links.is-style-logos-only) .wp-social-link-{$safe_key} {";
            $css .= "color: {$safe_color};";
            $css .= "}";
        }

        if (empty($css)) {
            return;
        }

        wp_add_inline_style(self::SOCIAL_LINKS_STYLE_HANDLE, $css);
        wp_enqueue_style(self::SOCIAL_LINKS_STYLE_HANDLE);
    }

    public static function EnqueueFrontendStyles()
    {
        if (has_block('core/social-links')) {
            self::EnqueueSocialIconStyles();
        }
    }
}
